import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Provider} from 'react-redux';
import { createStore, applyMiddleware, compose } from 'redux';
import { createLogger } from 'redux-logger';
import App from './containers/App';
import registerServiceWorker from './registerServiceWorker';
import 'tachyons';
import { searchRobots } from './reducers';

const logger = createLogger();// redux-logger
//Below
// You need compose() from redux to get addon to work with middleware
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(searchRobots, composeEnhancers(applyMiddleware(logger)));
// const store = createStore (searchRobots, applyMiddleware(logger));
// Below 
// The provider wraps the app and is responsible for providing the store to all the components in APP
ReactDOM.render(
    <Provider store={store}>
        <App /> 
    </Provider>,document.getElementById('root'));


registerServiceWorker();
